import React from "react";
import './Button.css'

export function Button(props) {
    return (
        <>
            <button className={`
                button
                ${props.operation ? 'operation' : ''}
                ${props.double ? 'double' : ''}
                ${props.tripl ? 'triple' : ''}
            `}>
                {props.label}
            </button>
        </>
    )
}
