import React from "react";
import './Button.css'

export function Button(props) {
    return (
        <button 
            onClick={e => props.click(props.innerHTML)}
            className={`
                button
                ${props.operation ? 'operation' : ''}
                ${props.double ? 'operation' : ''}
                ${props.triple ? 'operation' : ''}
            `}>
            {props.label}
        </button>

    )
}
